script:
frappe.ui.form.on('Item', {
	refresh(frm) {
	    if(!frm.doc.custom_classificationcode) {
    	    frappe.db.get_single_value('Ebarimt Settings', 'default_classification_code')
             .then(default_c_code => {
                frm.set_value("custom_classificationcode", default_c_code);
             })
	    }
    }
})
